import "./App.css";
import React, { useState, useEffect } from "react";
import { marked } from "marked";
import useLocalStorage from "./useLocalStorage";

const App = () => {
  const [code, setCode] = useLocalStorage("Messages", "## Hello");
  const [compiled, setCompiled] = useLocalStorage(
    "data",
    '<h2 id="hello">Hello</h2>'
  );
  const [write, setWrite] = useState([]);
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/posts?_limit=10')
       .then((response) => response.json())
       .then((data) => {
          console.log(data);
          setWrite(data);
       })
       .catch((err) => {
          console.log(err.message);
       });
 }, []);
  const [tabIndex, setTabIndex] = useState(1);
  const [hide, hidePreview] = useState(true);

  const openMD = () => {
    console.log(0);
    hidePreview(true);
  };

  const openPreview = () => {
    console.log(0);
    hidePreview(false);
  };

  const openDocs = () => {
    console.log("Docs");
    hidePreview(true);
  };

  const handleChange = (e) => {
    setCode(e.target.value);
    setCompiled(marked.parse(e.target.value));
    // setWrite(e.target.value);`
  };

  return (
    <>
      <h1>MarkDown Previewer React App</h1>
      <div className="container">
        <div className="btns">
          <button onClick={() => setTabIndex(1)} className="btn">
            MarkDown
          </button>
          <button onClick={() => setTabIndex(2)}>Preview</button>
          <button  onClick={() => setTabIndex(3)} className="btn">
            Docs
          </button>
        </div>
        {/* {hide ? (
          <div>
            <textarea onChange={handleChange} value={code} />
          </div>
        ) : (
          <div>
            <textarea value={compiled} />
          </div>
        )} */}
        {/* <div style={hide? {display:"none"} : {display:"block"}}>
          <textarea value={data}/>
        </div> */}
        {
            tabIndex === 1 && (<div><textarea onChange={handleChange} value={code} /></div>)}
            {
            tabIndex === 2 && (<div><textarea value={compiled} /></div>)}
            {
            tabIndex === 3 && (<div>{writes.map((write) => {
              return (
                 <div className="post-card" key={write.id}>
                    <h2 className="post-title">{write.title}</h2>
                    <p className="post-body">{write.body}</p>
                    <div className="button">
                    <div className="delete-btn">Delete</div>
                    </div>
                 </div>
              );
           })}
           {/* <textarea value={write}/> */}
           </div>)}
      </div>
    </>
  );
};

export default App;
